sp_widget.client_script:
function($rootScope, $scope) {
  /* widget controller */
  var c = this;
	for(var property in c.data){
			console.log('c.data1 ' + property +' : '+c.data[property]);
		}
	c.selectItem = function(idx){
		console.log('check idx..'+idx+'...'+c.data.notes[idx].number);
		var id = c.data.notes[idx].sys_id;
		$rootScope.noteId = id;
		$rootScope.$emit('selectNote',id);
	}
	$rootScope.$on('updateNote',function(event,data){
		console.log('in on event..'+$rootScope.noteId1+'...'+data);
	});
}

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	//console.log('check logged in user...'+$sp.getUserInitials());
	data.notes = [];
	var notesGr = new GlideRecord('x_snc_createnotes_note');
	notesGr.addQuery('user',gs.getUser().getID());
	notesGr.orderByDesc('sys_created_on');
	notesGr.query();
	while(notesGr.next()){
    var noteObj = {};
		$sp.getRecordDisplayValues(noteObj,notesGr,'number,title,sys_id');
		noteObj.note = notesGr.getValue('note').slice(0,20);
		data.notes.push(noteObj);
		console.log('data.notes = '+data.notes);
	}
})();
